// src/Posts.js - FIXED VERSION
import React, { useState, useEffect } from "react";
import PostCard from "./PostCard";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  query,
  orderBy,
  onSnapshot,
  serverTimestamp,
} from "firebase/firestore";

function Posts({ user, profile }) {
  const [postText, setPostText] = useState("");
  const [posts, setPosts] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Real-time posts listener
  useEffect(() => {
    const postsCollection = collection(db, "posts");
    const q = query(postsCollection, orderBy("createdAt", "desc"));

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const postsList = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setPosts(postsList);
    });

    return () => unsubscribe();
  }, []);

  // ðŸ“ Handle new post - FIXED VERSION
  const handlePost = async (e) => {
    e.preventDefault();
    
    if (!postText.trim()) {
      alert("Please type something before sending!");
      return;
    }

    try {
      setIsSubmitting(true);
      const postsCollection = collection(db, "posts");

      // SAFE DATA PREPARATION - Handle undefined values
      const postData = {
        text: postText.trim(),
        userId: user?.uid || "unknown",
        username: profile?.username || "Anonymous",
        // Only include userPhoto if it exists and is not undefined/null
        ...(profile?.photoURL && { userPhoto: profile.photoURL }),
        // Only include userGender if it exists and is not undefined/null
        ...(profile?.gender && { userGender: profile.gender }),
        createdAt: serverTimestamp(),
        timestamp: new Date().toISOString(),
      };

      console.log("Sending post data:", postData);

      await addDoc(postsCollection, postData);

      setPostText("");
      
      // Success animation
      const button = document.getElementById("pulse-button");
      if (button) {
        button.classList.add("bg-green-500");
        setTimeout(() => {
          button.classList.remove("bg-green-500");
        }, 500);
      }
      
    } catch (err) {
      console.error("Error adding post:", err);
      alert(`Failed to send pulse: ${err.message}`);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="bg-happy-bg/60 backdrop-blur-md p-6 rounded-2xl border border-happy-pulse/20 shadow-xl">
      {/* Post Input */}
      <form onSubmit={handlePost} className="mb-8">
        <div className="relative group">
          <input
            type="text"
            placeholder="What's pulsing through your mind?"
            value={postText}
            onChange={(e) => setPostText(e.target.value)}
            className="w-full p-4 pl-6 pr-32 bg-gray-900/70 border-2 border-happy-pulse/40 rounded-2xl text-white placeholder-gray-400 focus:outline-none focus:border-happy-pulse focus:ring-2 focus:ring-happy-pulse/50 text-lg transition-all duration-300"
          />
          
          <button
            type="submit"
            id="pulse-button"
            disabled={!postText.trim() || isSubmitting}
            className={`
              absolute right-2 top-1/2 transform -translate-y-1/2 
              px-6 py-2.5 rounded-full font-semibold 
              transition-all duration-300 flex items-center gap-2
              ${!postText.trim() || isSubmitting
                ? "bg-gray-800 text-gray-400 cursor-not-allowed" 
                : "bg-gradient-to-r from-happy-pulse to-happy-purple text-white hover:shadow-lg hover:scale-105 active:scale-95 cursor-pointer animate-pulse"
              }
            `}
          >
            {isSubmitting ? (
              <>
                <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                <span>Pulsing...</span>
              </>
            ) : (
              <>
                <span className="text-xl">âœ¨</span>
                <span>Pulse It</span>
              </>
            )}
          </button>
        </div>
        
        <div className="flex justify-between items-center mt-2">
          <p className="text-sm text-gray-400">
            Press Enter or click "Pulse It" to send
          </p>
          <span className={`text-xs ${postText.length > 250 ? 'text-red-400' : 'text-gray-400'}`}>
            {postText.length}/280
          </span>
        </div>
      </form>

      {/* Posts Header */}
      <div className="flex items-center mb-6">
        <h2 className="text-2xl font-bold text-white mr-4">
          ðŸ“¬ Activity Pulse
        </h2>
        <div className="flex-1 h-1 bg-gradient-to-r from-happy-pulse via-happy-teal to-transparent rounded-full"></div>
        <span className="ml-4 px-3 py-1 bg-happy-pulse/20 text-happy-teal rounded-full text-sm font-medium">
          {posts.length} pulse{posts.length !== 1 ? 's' : ''}
        </span>
      </div>

      {/* Posts List */}
      <div className="space-y-4">
        {posts.length === 0 ? (
          <div className="text-center py-12">
            <div className="text-6xl mb-4 opacity-50 animate-float">ðŸ’­</div>
            <p className="text-gray-400 text-lg">No pulses yet</p>
            <p className="text-gray-500 mt-2">Start the conversation!</p>
          </div>
        ) : (
          posts.map((post) => (
            <PostCard key={post.id} post={post} />
          ))
        )}
      </div>
    </div>
  );
}

export default Posts;
