// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./firebase";
import "./App.css";

// Import components
import Profile from "./Profile";
import Signup from "./Signup";
import Login from "./Login";
import Navigation from "./Navigation"; // Create this or use existing

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (firebaseUser) => {
      console.log("Auth state changed:", firebaseUser?.email);
      setUser(firebaseUser);
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleSignup = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    auth.signOut();
    setUser(null);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-happy-dark flex items-center justify-center">
        <div className="text-center">
          <div className="relative">
            <div className="w-16 h-16 border-4 border-happy-pulse border-t-transparent rounded-full animate-spin mx-auto"></div>
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="w-8 h-8 bg-happy-pulse rounded-full animate-ping"></div>
            </div>
          </div>
          <p className="mt-4 text-happy-teal font-medium">Syncing pulse...</p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        {/* Add Navigation if you want */}
        {/* <Navigation user={user} /> */}

        <Routes>
          <Route 
            path="/login" 
            element={
              !user ? (
                <Login onLogin={handleLogin} />
              ) : (
                <Navigate to="/profile" />
              )
            } 
          />

          <Route 
            path="/signup" 
            element={
              !user ? (
                <Signup onSignup={handleSignup} />
              ) : (
                <Navigate to="/profile" />
              )
            } 
          />

          <Route 
            path="/profile" 
            element={
              user ? (
                <Profile user={user} onLogout={handleLogout} />
              ) : (
                <Navigate to="/login" />
              )
            } 
          />

          <Route 
            path="/" 
            element={
              user ? (
                <Navigate to="/profile" />
              ) : (
                <Navigate to="/login" />
              )
            } 
          />

          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
