// src/Posts.js
import React, { useState, useEffect } from "react";
import PostCard from "./PostCard";

import { db } from "./firebase";
import {
  collection,
  addDoc,
  query,
  orderBy,
  onSnapshot,
  serverTimestamp,
} from "firebase/firestore";

function Posts({ user, profile }) {
  const [postText, setPostText] = useState("");
  const [posts, setPosts] = useState([]);

  // ðŸ”¥ Real-time posts listener
  useEffect(() => {
    const postsCollection = collection(db, "posts");
    const q = query(postsCollection, orderBy("createdAt", "desc"));

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const postsList = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setPosts(postsList);
    });

    return () => unsubscribe();
  }, []);

  // ðŸ“ Handle new post
  const handlePost = async (e) => {
    e.preventDefault();
    if (!postText) return;

    try {
      const postsCollection = collection(db, "posts");

      await addDoc(postsCollection, {
        text: postText,
        userId: user.uid,
        username: profile.username,
        userPhoto: profile.photoURL,
        userGender: profile.gender,
        createdAt: serverTimestamp(),
      });

      setPostText("");
    } catch (err) {
      console.error("Error adding post:", err);
    }
  };

  return (
    <div className="bg-happy-bg/60 backdrop-blur-md p-6 rounded-2xl border border-happy-pulse/20 shadow-xl">
      {/* Post Input */}
      <form onSubmit={handlePost} className="mb-8">
        <div className="relative group">
          <input
            type="text"
            placeholder="What's pulsing through your mind?"
            value={postText}
            onChange={(e) => setPostText(e.target.value)}
            className="w-full p-4 pl-6 pr-32 bg-gray-900/50 border-2 border-happy-pulse/30 rounded-2xl text-white placeholder-gray-400 focus:outline-none focus:border-happy-pulse focus:ring-2 focus:ring-happy-pulse/30 text-lg transition-all duration-300 group-hover:border-happy-pulse/50"
          />
          <button
            type="submit"
            disabled={!postText}
            className={`absolute right-2 top-1/2 transform -translate-y-1/2 px-6 py-2.5 rounded-full font-semibold transition-all duration-300 ${
              postText 
                ? "bg-gradient-to-r from-happy-pulse to-happy-purple text-white hover:shadow-lg hover:scale-105"
                : "bg-gray-800 text-gray-500 cursor-not-allowed"
            }`}
          >
            {postText ? "âœ¨ Pulse It" : "ðŸ’¤"}
          </button>
        </div>
      </form>

      {/* Posts Header */}
      <div className="flex items-center mb-6">
        <h2 className="text-2xl font-bold text-white mr-4">
          ðŸ“¬ Activity Pulse
        </h2>
        <div className="flex-1 h-1 bg-gradient-to-r from-happy-pulse via-happy-teal to-transparent rounded-full"></div>
        <span className="ml-4 px-3 py-1 bg-happy-pulse/20 text-happy-teal rounded-full text-sm font-medium">
          {posts.length} pulses
        </span>
      </div>

      {/* Posts List */}
      <div className="space-y-4">
        {posts.length === 0 ? (
          <div className="text-center py-12">
            <div className="text-6xl mb-4 opacity-50">ðŸ’­</div>
            <p className="text-gray-400 text-lg">No pulses yet. Be the first to start the conversation!</p>
            <p className="text-gray-500 mt-2">Your message will appear here as a pulse.</p>
          </div>
        ) : (
          posts.map((post) => (
            <PostCard key={post.id} post={post} />
          ))
        )}
      </div>
    </div>
  );
}

export default Posts;
