// src/Posts.js
import React, { useState, useEffect } from "react";
import { db } from "./firebase";
import { collection, addDoc, getDocs, query, orderBy, serverTimestamp } from "firebase/firestore";

function Posts({ user }) {
  const [postText, setPostText] = useState("");
  const [posts, setPosts] = useState([]);

  const postsCollection = collection(db, "posts");

  // Fetch posts from Firestore
  const fetchPosts = async () => {
    const q = query(postsCollection, orderBy("createdAt", "desc"));
    const snapshot = await getDocs(q);
    const postsList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setPosts(postsList);
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  // Handle new post
  const handlePost = async (e) => {
    e.preventDefault(); // prevent page reload
    if (!postText) return;

    try {
      await addDoc(postsCollection, {
        text: postText,
        userEmail: user.email,
        createdAt: serverTimestamp() // correct timestamp
      });
      setPostText(""); // clear input
      fetchPosts(); // refresh feed
    } catch (err) {
      console.error("Error adding post:", err);
    }
  };

  return (
    <div className="bg-white p-6 rounded shadow-md">
      <form onSubmit={handlePost} className="mb-4">
        <input
          type="text"
          placeholder="What's on your mind?"
          value={postText}
          onChange={(e) => setPostText(e.target.value)}
          className="w-full p-2 border rounded mb-2"
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Post
        </button>
      </form>

      <h2 className="text-xl font-bold mb-2">Posts</h2>
      <div className="space-y-2">
        {posts.map(post => (
          <div key={post.id} className="border p-2 rounded">
            <p>{post.text}</p>
            <small className="text-gray-500">by {post.userEmail}</small>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Posts;
