// src/Login.js - WITHOUT REACT-ROUTER-DOM
import React, { useState } from "react";
import { signInWithEmailAndPassword } from "firebase/auth";
import { doc, updateDoc, getDoc } from "firebase/firestore";
import { auth, db } from "./firebase";

function Login({ onLoginSuccess }) { // Add this prop
  const [formData, setFormData] = useState({
    email: "",
    password: ""
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const userCredential = await signInWithEmailAndPassword(
        auth,
        formData.email,
        formData.password
      );

      const user = userCredential.user;

      // Update online status
      const userRef = doc(db, "users", user.uid);
      const userSnap = await getDoc(userRef);
      
      if (userSnap.exists()) {
        await updateDoc(userRef, {
          isOnline: true,
          lastLogin: new Date().toISOString()
        });
      }

      // Call parent function to handle successful login
      if (onLoginSuccess) {
        onLoginSuccess(user);
      }

      // Show success message
      setError(""); // Clear any errors
      alert("Login successful! Redirecting...");
      
    } catch (err) {
      console.error("Login error:", err);
      setError("Invalid email or password");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-gray-900 via-black to-purple-950">
      <div className="bg-happy-bg/80 backdrop-blur-lg rounded-2xl p-8 border border-happy-pulse/30 shadow-2xl w-96">
        <h1 className="text-3xl font-bold mb-6 text-center text-white">
          ✨ HappyHour Login
        </h1>
        
        {error && (
          <div className="mb-4 p-3 bg-red-900/50 border border-red-500 rounded-lg">
            <p className="text-red-200">{error}</p>
          </div>
        )}
        
        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
            className="w-full p-3 bg-gray-900/70 border border-happy-pulse/30 rounded-xl text-white"
          />
          
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
            className="w-full p-3 bg-gray-900/70 border border-happy-pulse/30 rounded-xl text-white"
          />
          
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 rounded-xl font-semibold ${
              loading ? "bg-gray-800" : "bg-gradient-to-r from-happy-pulse to-happy-purple text-white"
            }`}
          >
            {loading ? "Logging in..." : "✨ Login"}
          </button>
        </form>
        
        <p className="mt-6 text-center text-gray-400">
          Don't have an account? <span className="text-happy-teal">Sign up</span>
        </p>
      </div>
    </div>
  );
}

export default Login;
