// src/Posts.js - DEBUG VERSION
import React, { useState, useEffect } from "react";
import PostCard from "./PostCard";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  query,
  orderBy,
  onSnapshot,
  serverTimestamp,
} from "firebase/firestore";

function Posts({ user, profile }) {
  const [postText, setPostText] = useState("");
  const [posts, setPosts] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [debug, setDebug] = useState("");

  // üî• Real-time posts listener
  useEffect(() => {
    console.log("Posts component mounted");
    const postsCollection = collection(db, "posts");
    const q = query(postsCollection, orderBy("createdAt", "desc"));

    const unsubscribe = onSnapshot(q, (snapshot) => {
      console.log("Posts updated:", snapshot.docs.length);
      const postsList = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setPosts(postsList);
    });

    return () => unsubscribe();
  }, []);

  // üìù DEBUG: Handle new post
  const handlePost = async (e) => {
    console.log("=== HANDLE POST STARTED ===");
    console.log("Event:", e.type);
    console.log("Post text:", postText);
    console.log("Post text length:", postText.length);
    console.log("Post text trimmed:", postText.trim());
    console.log("Is submitting:", isSubmitting);
    console.log("User:", user?.uid);
    console.log("Profile:", profile?.username);
    
    e.preventDefault();
    e.stopPropagation();
    
    setDebug(`Clicked at: ${new Date().toLocaleTimeString()}`);
    
    if (!postText.trim()) {
      console.log("ERROR: Post text is empty after trim");
      setDebug("Text is empty");
      return;
    }
    
    if (isSubmitting) {
      console.log("ERROR: Already submitting");
      setDebug("Already submitting");
      return;
    }

    try {
      setIsSubmitting(true);
      setDebug("Submitting to Firebase...");
      console.log("Attempting to submit to Firebase...");
      
      const postsCollection = collection(db, "posts");
      
      const postData = {
        text: postText.trim(),
        userId: user.uid,
        username: profile.username,
        userPhoto: profile.photoURL,
        userGender: profile.gender,
        createdAt: serverTimestamp(),
        timestamp: new Date().toISOString(),
      };
      
      console.log("Post data:", postData);
      
      const docRef = await addDoc(postsCollection, postData);
      console.log("‚úÖ Document written with ID:", docRef.id);
      
      setPostText("");
      setIsSubmitting(false);
      setDebug(`Success! Posted at ${new Date().toLocaleTimeString()}`);
      
      // Play success sound (optional)
      if (typeof window !== 'undefined') {
        const audio = new Audio('https://assets.mixkit.co/sfx/preview/mixkit-unlock-game-notification-253.mp3');
        audio.volume = 0.3;
        audio.play().catch(() => {});
      }
      
    } catch (err) {
      console.error("‚ùå Error adding post:", err);
      console.error("Error code:", err.code);
      console.error("Error message:", err.message);
      setIsSubmitting(false);
      setDebug(`Error: ${err.message}`);
      
      alert(`Failed to send pulse: ${err.message}`);
    }
  };

  // Test function to bypass form
  const testDirectPost = async () => {
    console.log("=== TEST DIRECT POST ===");
    try {
      const postsCollection = collection(db, "posts");
      await addDoc(postsCollection, {
        text: "Test message from debug",
        userId: "test-user",
        username: "Debug Bot",
        createdAt: serverTimestamp(),
      });
      console.log("‚úÖ Test post successful!");
      setDebug("Test post successful!");
    } catch (err) {
      console.error("‚ùå Test post failed:", err);
      setDebug(`Test failed: ${err.message}`);
    }
  };

  return (
    <div className="bg-happy-bg/60 backdrop-blur-md p-6 rounded-2xl border border-happy-pulse/20 shadow-xl">
      {/* DEBUG PANEL */}
      <div className="mb-4 p-3 bg-black/50 rounded-lg border border-yellow-500/50">
        <div className="flex justify-between items-center">
          <h3 className="text-yellow-300 font-bold">üîß Debug Panel</h3>
          <button 
            onClick={testDirectPost}
            className="px-3 py-1 bg-yellow-600 text-white text-sm rounded hover:bg-yellow-700"
          >
            Test Firebase
          </button>
        </div>
        <div className="mt-2 text-sm">
          <p className="text-gray-300">Status: <span className="text-green-400">{debug || "Ready"}</span></p>
          <p className="text-gray-300">Text: "{postText}" (length: {postText.length})</p>
          <p className="text-gray-300">User: {user?.uid ? "‚úì Logged in" : "‚úó No user"}</p>
          <p className="text-gray-300">Posts loaded: {posts.length}</p>
        </div>
      </div>

      {/* Post Input */}
      <form onSubmit={handlePost} className="mb-8">
        <div className="relative group">
          <input
            type="text"
            placeholder="What's pulsing through your mind?"
            value={postText}
            onChange={(e) => {
              console.log("Input changed:", e.target.value);
              setPostText(e.target.value);
            }}
            className="w-full p-4 pl-6 pr-40 bg-gray-900/70 border-2 border-happy-pulse/40 rounded-2xl text-white placeholder-gray-400 focus:outline-none focus:border-happy-pulse focus:ring-2 focus:ring-happy-pulse/50 text-lg transition-all duration-300"
            id="post-input"
          />
          
          <button
            type="submit"
            onClick={(e) => {
              console.log("Button clicked directly");
              handlePost(e);
            }}
            className={`
              absolute right-2 top-1/2 transform -translate-y-1/2 
              px-6 py-2.5 rounded-full font-semibold 
              transition-all duration-300 flex items-center gap-2
              min-w-[120px] justify-center
              ${!postText.trim() || isSubmitting
                ? "bg-gray-800 text-gray-400 cursor-not-allowed" 
                : "bg-gradient-to-r from-happy-pulse to-happy-purple text-white hover:shadow-lg hover:scale-105 active:scale-95 cursor-pointer"
              }
            `}
            id="pulse-button"
          >
            {isSubmitting ? (
              <>
                <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                <span>Pulsing...</span>
              </>
            ) : (
              <>
                <span className="text-xl">‚ú®</span>
                <span>Pulse It</span>
              </>
            )}
          </button>
        </div>
        
        <p className="mt-2 text-sm text-gray-400">
          Type something and check console (F12) for errors
        </p>
      </form>

      {/* Posts Header */}
      <div className="flex items-center mb-6">
        <h2 className="text-2xl font-bold text-white mr-4">
          üì¨ Activity Pulse
        </h2>
        <div className="flex-1 h-1 bg-gradient-to-r from-happy-pulse via-happy-teal to-transparent rounded-full"></div>
        <span className="ml-4 px-3 py-1 bg-happy-pulse/20 text-happy-teal rounded-full text-sm font-medium">
          {posts.length} pulse{posts.length !== 1 ? 's' : ''}
        </span>
      </div>

      {/* Posts List */}
      <div className="space-y-4">
        {posts.length === 0 ? (
          <div className="text-center py-12">
            <div className="text-6xl mb-4 opacity-50">üí≠</div>
            <p className="text-gray-400 text-lg">No pulses yet</p>
            <p className="text-gray-500 mt-2">Try the "Test Firebase" button above</p>
          </div>
        ) : (
          posts.map((post) => (
            <PostCard key={post.id} post={post} />
          ))
        )}
      </div>
    </div>
  );
}

export default Posts;
