// src/PostCard.js - Updated to handle missing data
import React from "react";
import Avatar from "./Avatar";

function PostCard({ post }) {
  // Format timestamp safely
  const formatTime = (timestamp) => {
    if (!timestamp) return "Just now";
    try {
      const date = timestamp.toDate();
      return date.toLocaleTimeString([], {
        hour: "2-digit",
        minute: "2-digit",
      });
    } catch (err) {
      return "Recently";
    }
  };

  // Safely get user data with defaults
  const username = post.username || "Anonymous";
  const userGender = post.userGender || "unknown";
  const userPhoto = post.userPhoto || null;

  return (
    <div className="bg-gray-900/50 backdrop-blur-sm rounded-xl p-5 border-l-4 border-happy-pulse hover:border-happy-teal transition-all duration-300 group hover:bg-gray-900/70">
      <div className="flex items-start space-x-4">
        {/* User Avatar with safe data */}
        <div className="flex-shrink-0">
          <Avatar
            username={username}
            gender={userGender}
            photoURL={userPhoto}
            size={50}
          />
        </div>

        {/* Post Content */}
        <div className="flex-1">
          <div className="flex justify-between items-start">
            <div>
              <h3 className="font-bold text-white group-hover:text-happy-teal transition-colors">
                {username}
              </h3>
              <p className="text-gray-400 text-sm">
                {formatTime(post.createdAt)}
              </p>
            </div>
            {/* Pulse indicator */}
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-happy-pulse rounded-full animate-pulse"></div>
              <span className="text-xs text-gray-500">Live</span>
            </div>
          </div>

          {/* Post Text */}
          <p className="mt-3 text-gray-200 text-lg leading-relaxed">
            {post.text}
          </p>

          {/* Interaction Bar */}
          <div className="mt-4 pt-3 border-t border-gray-800 flex space-x-4">
            <button className="flex items-center space-x-2 text-gray-400 hover:text-happy-pulse transition-colors">
              <span className="text-xl">‚ù§Ô∏è</span>
              <span className="text-sm">Pulse</span>
            </button>
            <button className="flex items-center space-x-2 text-gray-400 hover:text-happy-teal transition-colors">
              <span className="text-xl">üí¨</span>
              <span className="text-sm">Reply</span>
            </button>
            <button className="flex items-center space-x-2 text-gray-400 hover:text-happy-purple transition-colors">
              <span className="text-xl">üîÑ</span>
              <span className="text-sm">Resonate</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PostCard;
