import Avatar from "./Avatar";

function PostCard({ post }) {
  if (!post) return null; // üõ° safety guard

  const username = post.username || "Unknown";
  const text = post.text || "";
  const views = typeof post.views === "number" ? post.views : 0;
  const comments =
    typeof post.commentsCount === "number" ? post.commentsCount : 0;

  let createdAtText = "Just now";
  if (post.createdAt && post.createdAt.toDate) {
    createdAtText = post.createdAt.toDate().toLocaleString();
  }

  return (
    <div className="bg-gray-300 rounded-xl p-4 space-y-3">
      {/* Header */}
      <div className="flex items-center space-x-3">
        <Avatar
          username={username}
          gender={post.gender || null}
          photoURL={post.photoURL || null}
          size={40}
        />

        <div>
          <p className="font-semibold">{username}</p>
          <p className="text-xs text-gray-500">{createdAtText}</p>
        </div>
      </div>

      {/* Body */}
      <div className="text-gray-800 break-words">{text}</div>

      {/* Footer */}
      <div className="flex justify-between text-sm text-gray-600">
        <span>üëÅ {views} views</span>
        <span>üí¨ {comments} comments</span>
      </div>
    </div>
  );
}

export default PostCard;
