// src/Profile.js
import React, { useEffect, useState } from "react";
import { doc, getDoc } from "firebase/firestore";
import { auth } from "./firebase";
import { db } from "./firebase";
import Posts from "./Posts";
import Avatar from "./Avatar";

function Profile({ user, onLogout }) {
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      const ref = doc(db, "users", user.uid);
      const snap = await getDoc(ref);

      if (snap.exists()) {
        setProfile(snap.data());
      }
    };

    fetchProfile();
  }, [user.uid]);

  if (!profile) return <p className="text-center mt-10">Loading profile...</p>;

  return (
    <div className="flex flex-col items-center min-h-screen bg-gray-100 p-6">
      <Avatar
        username={profile.username}
        gender={profile.gender}
        photoURL={profile.photoURL}
        size={100}
      />

      <h1 className="text-3xl font-bold mt-4">Welcome, {profile.username}!</h1>

      <p className="text-gray-600 mb-4">{profile.email}</p>

      <button
        onClick={onLogout}
        className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 mb-6"
      >
        Logout
      </button>
      <div className="bg-purple-500 text-white p-6 rounded-xl mb-4">
        Tailwind finally works ðŸŽ‰
      </div>

      <div className="w-full max-w-md">
        <Posts user={user} profile={profile} />
      </div>
    </div>
  );
}

export default Profile;
