// src/Profile.js
import React, { useEffect, useState } from "react";
import { doc, getDoc, updateDoc } from "firebase/firestore";
import { auth } from "./firebase";
import { db } from "./firebase";
import Posts from "./Posts";
import Avatar from "./Avatar";
import OnlineUsers from "./OnlineUsers"; // ADD THIS
import { useOnlineStatus, setOffline } from "./useOnlineStatus"; // ADD THIS
import { useNavigate } from "react-router-dom";

function Profile({ user, onLogout }) {
  const [profile, setProfile] = useState(null);
  const navigate = useNavigate();

  // Track online status
  useOnlineStatus(user?.uid);

  useEffect(() => {
    const fetchProfile = async () => {
      const ref = doc(db, "users", user.uid);
      const snap = await getDoc(ref);

      if (snap.exists()) {
        setProfile(snap.data());
      }
    };

    fetchProfile();
  }, [user.uid]);

  const handleLogout = async () => {
    try {
      // Set user as offline
      if (user?.uid) {
        await setOffline(user.uid);
      }
      
      await onLogout();
      navigate("/login");
    } catch (err) {
      console.error("Logout error:", err);
    }
  };

  if (!profile) return (
    <div className="min-h-screen bg-happy-dark flex items-center justify-center">
      <div className="text-center">
        <div className="relative">
          <div className="w-16 h-16 border-4 border-happy-pulse border-t-transparent rounded-full animate-spin mx-auto"></div>
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="w-8 h-8 bg-happy-pulse rounded-full animate-ping"></div>
          </div>
        </div>
        <p className="mt-4 text-happy-teal font-medium">Loading your pulse...</p>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-happy-gradient bg-happy-pattern p-4 md:p-6">
      {/* Animated pulse lines in background */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        {[...Array(3)].map((_, i) => (
          <div
            key={i}
            className="absolute h-0.5 bg-gradient-to-r from-transparent via-happy-pulse/20 to-transparent animate-pulse-slow"
            style={{
              top: `${30 + i * 20}%`,
              width: "100%",
              animationDelay: `${i * 0.7}s`,
            }}
          />
        ))}
      </div>

      <div className="max-w-6xl mx-auto">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Left Column - Profile Card */}
          <div className="lg:col-span-1">
            <div className="bg-happy-bg/80 backdrop-blur-lg rounded-2xl p-6 border border-happy-pulse/30 shadow-2xl sticky top-6">
              <div className="flex flex-col items-center">
                <div className="relative">
                  <Avatar
                    username={profile.username}
                    gender={profile.gender}
                    photoURL={profile.photoURL}
                    size={120}
                  />
                  {/* Online indicator */}
                  <div className="absolute bottom-2 right-2">
                    <div className="w-4 h-4 bg-green-500 rounded-full border-2 border-happy-dark"></div>
                    <div className="absolute inset-0 bg-green-500 rounded-full animate-ping opacity-75"></div>
                  </div>
                </div>

                <h1 className="text-3xl font-bold mt-6 text-white bg-gradient-to-r from-happy-pulse to-happy-teal bg-clip-text text-transparent">
                  {profile.username}
                </h1>

                <div className="relative mt-3">
                  <p className="text-gray-300 text-lg">{profile.email}</p>
                  <div className="absolute -bottom-2 left-1/4 right-1/4 h-0.5 bg-gradient-to-r from-transparent via-happy-pulse to-transparent transform -translate-x-1/4"></div>
                </div>

                {/* User Stats */}
                <div className="grid grid-cols-2 gap-4 mt-6 w-full">
                  <div className="bg-gray-900/50 p-3 rounded-xl text-center">
                    <p className="text-2xl font-bold text-happy-teal">ðŸŽ¯</p>
                    <p className="text-sm text-gray-400">Pulses</p>
                  </div>
                  <div className="bg-gray-900/50 p-3 rounded-xl text-center">
                    <p className="text-2xl font-bold text-happy-purple">ðŸ‘¥</p>
                    <p className="text-sm text-gray-400">Friends</p>
                  </div>
                </div>

                {/* Bio */}
                {profile.bio && (
                  <div className="mt-6 w-full">
                    <h3 className="text-white font-bold mb-2">Bio</h3>
                    <p className="text-gray-400 bg-gray-900/30 p-4 rounded-xl">
                      {profile.bio}
                    </p>
                  </div>
                )}

                <button
                  onClick={handleLogout}
                  className="mt-8 w-full bg-gradient-to-r from-happy-pulse to-happy-purple text-white px-8 py-3 rounded-full hover:from-happy-purple hover:to-happy-pulse font-semibold shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1"
                >
                  âœ¨ Logout
                </button>
              </div>
            </div>
          </div>

          {/* Middle Column - Posts */}
          <div className="lg:col-span-2">
            <div className="space-y-6">
              <Posts user={user} profile={profile} />
              
              {/* Online Users Section */}
              <OnlineUsers currentUserId={user.uid} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
